targets.expr:
sum (
  vector(0) and on() (
    (sum(gitlab_component_shard_ops:rate_5m{component="api_requests", shard=~"$shard"}) by (shard) > 0)
  )
  or vector(1)
)

sum by (executor_stage, stage, shard) (
  gitlab_runner_jobs{state="running", shard=~"$shard"}
)

sum by (shard) (
  rate(
    gitlab_runner_acceptable_job_queuing_duration_exceeded_total{shard=~"$shard"}[$__rate_interval]
  )
)
/
sum by (shard) (
  rate(
    gitlab_runner_jobs_total{shard=~"$shard"}[$__rate_interval]
  )
)
