targets.expr:
clamp_min(clamp_max((
  (
    sum by (scope) (
  rate(apiserver_request_duration_seconds_bucket{job="apiserver",le="+Inf",scope!="",verb!~"^(?:CONNECT|WATCHLIST|WATCH|PROXY)$"}[$__interval])
)

    -
    (
      sum by (scope) (
  rate(apiserver_request_duration_seconds_bucket{job="apiserver",le="1",scope!="",verb!~"^(?:CONNECT|WATCHLIST|WATCH|PROXY)$"}[$__interval])
)

    )
  )
  / ignoring(scope) group_left()
  (
    sum by () (
  rate(apiserver_request_duration_seconds_bucket{job="apiserver",le="+Inf",scope!="",verb!~"^(?:CONNECT|WATCHLIST|WATCH|PROXY)$"}[$__interval])
)

  )
) > 0
,1),0)

